mapper.select:
select l.*,c.*,p.*,g.*,g.sex AS sex1,g.IDCard AS IDCard1, g.telephone AS telephone1,g.address AS address1 from loans l LEFT JOIN company c on l.loan_cid=c.cid LEFT JOIN pledge p on l.pledge_id=p.id LEFT JOIN guarantor g ON l.guarantor_id=g.gid
and (l.loan_id like "%"#{parameter}"%" or c.cname like "%"#{parameter}"%" or l.loan_money like "%"#{parameter}"%" or p.pledge_name like "%"#{parameter}"%" or g.gname like "%"#{parameter}"%" or date_format(l.apply_time,'%Y-%m-%d %H:%i:%s') like "%"#{parameter}"%")
and l.loans_status=0 limit ${start},${pagesize}
